script:
let company_class_element = root_element.querySelector('.company-name');

const company = frappe.boot?.user?.defaults?.company;

if (company) {
  frappe.db.get_value("Company", company, "company_name")
    .then(r => {
      company_class_element.textContent =
        r.message.company_name || company;
    });
}

let user_class_element = root_element.querySelector('.user-name');

const user = frappe.session.user;

if (!user) {
  user_class_element.textContent = "Guest";
} else {
  frappe.db.get_value("User", user, "full_name")
    .then(r => {
      user_class_element.textContent =
        r.message.full_name || user;
    });
}

style:
/* BASE */
.home-wrapper {
  padding: 24px;
  max-width: 1400px;
  margin: auto;
  background: #f6f7fb;
  font-family: Inter, system-ui, sans-serif;
}

/* HERO */
.hero-card {
  background: linear-gradient(135deg, #e7dbff, #f3eeff);
  border-radius: 14px;
  padding: 28px 32px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 28px;
}

.hero-left h1 {
  font-size: 34px;
  margin: 12px 0;
}

.hero-left p {
  color: #555;
  max-width: 460px;
}

.badge {
  background: #7c4dff;
  color: white;
  padding: 6px 14px;
  border-radius: 20px;
  font-size: 12px;
}

.hero-right {
  display: flex;
}

/* MERGED INFO PILL */
.info-pill-merged {
  display: flex;
  background: white;
  border-radius: 14px;
  box-shadow: 0 6px 16px rgba(0,0,0,.08);
  overflow: hidden;
}

.pill-left,
.pill-right {
  padding: 10px 14px;
  font-size: 13px;
  display: flex;
  gap: 6px;
  align-items: flex-start;
  white-space: normal;
  word-break: break-word;
}

.pill-divider {
  width: 1px;
  background: #e5e5ef;
}

/* ACTION ROW */
.action-row {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 20px;
  margin-bottom: 24px;
}

.action-card {
  background: white;
  border-radius: 14px;
  padding: 22px;
  box-shadow: 0 10px 30px rgba(0,0,0,.08);
}

/* CARD HEADER */
.card-head {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.card-title {
  display: flex;
  align-items: center;
  gap: 12px;
}

.card-title h3 {
  margin: 0;
  font-size: 18px;
  font-weight: 600;
}

/* ICON */
.icon-box {
  width: 36px;
  height: 36px;
  border-radius: 10px;
  background: #f1ecff;
  color: #7c4dff;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 18px;
}

/* TAG */
.tag {
  padding: 6px 12px;
  border-radius: 20px;
  font-size: 12px;
  color: white;
}

.selling,
.stock,
.projects {
  background: #7c4dff;
}

/* CARD ACTIONS */
.card-actions {
  display: flex;
  gap: 10px;
  margin-top: 18px;
}

/* BUTTONS */
.btn {
  padding: 10px 18px;
  border-radius: 10px;
  font-size: 13px;
  border: none;
  cursor: pointer;
}

.primary {
  background: #7c4dff;
  color: white;
  width:125px;
}

.ghost {
  background: #f1f1f6;
  color: #333;
  height: 50px;
  width: 125px;
}

/* MODULE PILLS */
.module-pills {
  display: flex;
  gap: 10px;
  flex-wrap: wrap;
  margin-bottom: 24px;
  font-weight: 600;
}

.module-pills span {
  background: #eee;
  padding: 8px 14px;
  border-radius: 10px;
  font-size: 13px;
}

.bottom-row {
  display: grid;
  grid-template-columns: 2fr 520px;
  gap: 1px;
  align-items: start;
}

/* WELCOME CARD */
.welcome-card {
  background: white;
  border-radius: 14px;
  padding: 10px;
  box-shadow: 0 14px 36px rgba(0,0,0,.1);
  display: flex;
  gap: 10px;
  align-items: center;
  flex-wrap: nowrap;
  min-height: 160px;
}

.welcome-left {
  display: flex;
  flex-direction: column;
  gap: 12px;
  flex: 0 1 auto;
  min-width: 0; 
}

.welcome-left h2 {
  font-size: 26px;
  margin: 0;
  line-height: 1.2;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.welcome-left p {
  font-size: 14px;
  color: #555;
  margin: 0;
  line-height: 1.5;
  overflow-wrap: anywhere;
}

.welcome-actions {
  display: flex;
  gap: 12px;
  margin-top: 12px;
  flex-wrap: wrap;
}

.welcome-right {
  flex: 0 0 160px;
  display: flex;
  justify-content: center;
  align-items: center;
}

.welcome-right {
    flex: 1 !important;
    display: flex !important;
    padding: 0 !important;
    margin: 0 !important;
    overflow: hidden !important;
    border-radius: 0 12px 12px 0;
}

/* Image scaling to fill */
.welcome-right img {
    border-radius: 20px !important;
    width: 100% !important;
    height: 100% !important;
    object-fit: cover !important;
    display: block !important;
}
